WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}

var orderId = '{!Service_Order__c.Id}';
var Name = '{!Service_Order__c.Name}';
var params =  {soId:orderId,orderNum:Name};

if(Name){
    try{
      var res = sforce.apex.execute("ServiceOrderStage_Ctrl","updateSOStage",params);
      alert('Successfully updated status. Please refresh your screen');
    }catch(err){
      var errorString = err.faultstring;
      var startIndex = errorString.indexOf('{');
      var endIndex = errorString.lastIndexOf('}')+1;
      var actualErrorMessage = JSON.parse(errorString.substring(startIndex,endIndex));

      alert(actualErrorMessage.cause+'\n'+actualErrorMessage.errorMessage);
    }

}else{
   alert('Service Order Status Must Not Be Empty');

}
